mapper.update:
    	UPDATE to_users          
	        SET USERPOINT = USERPOINT + #{dePoint}           
	    WHERE userId = #{userId}

UPDATE to_users          
    SET USERPOINT = USERPOINT - #{wiPoint}           
WHERE userId = #{userId}

mapper.delete:
DELETE FROM dw_record 
WHERE userId = #{userId}
  AND dwSeq = #{dwSeq}
