mapper.select:
SELECT X.*,Y.* FROM( SELECT T.gameDate ,T.gameHome ,T.gameAway ,T.gameHs ,T.gameAs ,NVL(DECODE(T.GAMERESULT, 1 , '홈승', 2 , '무승부', 3 , '원정승'),'대기중') as gameResult FROM( SELECT hr.* ,ROW_NUMBER() OVER(ORDER BY GAMEDATE ASC) AS rnum FROM cross hr WHERE months_between(sysdate, TO_CHAR(gameDate, 'YY/MM/DD')) < 2
)T WHERE rnum BETWEEN (#{page_size} * (#{page_num}-1)+1) AND (#{page_size} * (#{page_num}-1)+#{page_size}) )X NATURAL JOIN ( SELECT COUNT(*) AS total_cnt FROM cross
)Y
