mapper.update:
	 UPDATE cus_service          
        SET cusCat   = #{cusCat}      
       ,cusTitle     = #{cusTitle}      
       ,cusContent   = #{cusContent}       
       ,cusModId     = #{cusModid}      
       ,cusModDt     = SYSDATE
    WHERE cusSeq    = #{cusSeq}

     UPDATE cus_service        
        SET   cusReply= #{cusReply}    
	    WHERE cusSeq    = #{cusSeq}

mapper.select:
SELECT X.*,Y.* FROM( SELECT T.cusSeq AS cusSeq, T.userId, T.cusCat, T.cusTitle, T.cusReply, TO_CHAR(T.cusRegDt,'YYYY-MM-DD') cusRegDt, T.rnum AS no FROM( SELECT cus.* ,ROW_NUMBER() OVER(ORDER BY cusRegDt desc ) AS rnum FROM cus_service cus
)T WHERE rnum BETWEEN (#{page_size} * (#{page_num}-1)+1) AND (#{page_size} * (#{page_num}-1)+#{page_size}) )X NATURAL JOIN ( SELECT COUNT(*) AS total_cnt FROM cus_service cus
)Y
