mapper.update:
		UPDATE to_users
		SET
    		userPoint = userPoint + #{betwPrice}
    	Where userId = #{userId}
    		AND
     			((SELECT betReturn 
        	FROM bet_history
        		WHERE betSeq = #{betSeq}
        		group by betSeq, betReturn)=0)

    	update bet_history
		SET betReturn = '1'
		where betSeq = #{betSeq}
